import React, { Fragment,useState,useEffect} from "react"
import PlayCover from "./playcover"
import {NavLink} from "react-router-dom"
import {useMusicurl} from "../store/action/index"
import {useSelector} from "react-redux"
function TopMusicList(props){
    let [show,setShow] = useState(false)

    // console.log(props.data)
    let [muslocation,setMusic] = useState(1)

    //获取音乐地址状态管理函数
    let getmusic = useMusicurl()

    let state = useSelector((state)=>{
        return state.musicReducer
    })
    let newurl;
    useEffect(()=>{
        let muc = new Promise((reslove,reject)=>{
            if(state.data.length===undefined){
                return reslove(setMusic)
            }
        })
        muc.then((res)=>{
           res(state.data.data[0].url)
        }).then((res)=>{
            console.log(muslocation)
        })
    },[state,muslocation])
   
    
    
    return(
        <Fragment>
            <ul className="notice-mucList">
                {props.data.length===0?"":(props.data.playlist.tracks.slice(0,10).map((item,index)=>{
                    return(
                        <li className="notice-list" id={show?"show":""} key={item.id} 
                            onMouseEnter={function(e){
                                let parentnode = e.target;
                                function Parent(){
                                    if(parentnode.parentElement.getAttribute("class")==="notice-mucList"){
                                      parentnode.querySelector(".num-show").style.display="block"
                                    }else{
                                        parentnode = parentnode.parentElement
                                        Parent()
                                    }
                                }
                                Parent()
                            }}
                            onMouseLeave={function(e){
                                let parentnode = e.target;
                                function Parent(){
                                    if(parentnode.parentElement.getAttribute("class")==="notice-mucList"){
                                        parentnode.querySelector(".num-show").style.display="none"
                                    }else{
                                        parentnode = parentnode.parentElement
                                        Parent()
                                    }
                                }
                                Parent()
                            }}
                            onClick={()=>{
                                getmusic(`/song/url?id=${item.id}`)
                            }}
                        >
                            <span className={index<3?"rednum":""}>{index+1}</span>
                            <NavLink  to="" className="num-music">{item.name}</NavLink>
                            <div className="num-show">
                                <PlayCover/>
                            </div>
                        </li>
                    )
                }))}
            </ul>
            <div className="notice more">
                <NavLink to="">查看更多></NavLink>
            </div>
        </Fragment>
    )
}

export default TopMusicList;
