import React, { Fragment, useEffect } from 'react';
import { Route, Switch } from 'react-router-dom';
import "./static/css/index.css"
import {headerRouter,childRender} from "./router/index"
import Header from "./component/header"
import {useSelector} from "react-redux"
import Play from "./component/play"
function App() {
let state = (useSelector((state)=>{
	return state
}))
let loading = state.bannerReducer.loading;
useEffect(()=>{
	//媒体标签和自适应单位设置
	let scri = createElement("script")
	scri.scr="./static/js/index.js"
	let head = document.querySelector("head")
	let piex = window.devicePixelRatio;
	let meta = document.createElement("meta")
	let scale = 1/piex;
	console.log()
	meta.name="viewport"
	meta.content="width='device-width,user-scalable=no,initial-scale="+scale+",minimum-scale="+scale+",maximum-scale="+scale+"'"
	head.appendChild(meta)
	head.appendChild(scri)
	let html = document.documentElement
	let dWidth = html.clientWidth;
	let font=dWidth/72 + "px"
	html.style.fontSize = font;

	//判断设备是否是移动设备
	let usedev = navigator.userAgent
	console.log(usedev)
	let dev = [
		"android",
		"iphone",
		"ipad",
		"symbianos",
		"mobile"
	]


	dev.map((item,index)=>{
		if(usedev.indexOf(item)!==-1){
			console.log(usedev)
		}
	})

})
return (
	<Fragment>
		<div className="App">
			<Play/>
			{/* <div className={loading?"loading":"loadingend"}>
				<p id="loading">Loading<span>.</span><span>.</span><span>.</span></p>
			</div> */}
			<Header/>
			<Switch>
				{headerRouter.map((item,index)=>{
					return(
						<Route
							key={index}
							path={item.path}
							exact={item.exact}
							render={(props)=>{
								return item.render(props)
							}}
						></Route>
					)
				})}
				{childRender.map((item,index)=>{
					return(
						<Route
							key={index}
							path={item.path}
							exact={item.exact}
							render={(props)=>{
								return item.render(props)
							}}
						></Route>
					)
				})}
			</Switch>
		</div>
	</Fragment>
  );
}
export default App;
