import React from "react"
import {Redirect} from "react-router-dom"
import Index from "../views/index/index"
import My from "../views/my/index"
import Friend from "../views/friend/index"
import Download from "../views/download/index"
import Album from "../views/album/index"
import Artist from "../views/artist/index"
import Djradio from "../views/djradio/index"
import PlayList from "../views/playlist/index"
import TopList from "../views/toplist/index"
let headerRouter = [
    {
        path:"/",
        exact:true,
        render(props){
            return <Redirect to="/index"/>
        }
    },{
        path:"/index/:type",
        exact:true,
        render(props){
            return <Redirect to="/index"/>
        }
    },{
        path:"/index",
        exact:true,
        render(props){
            return <Index/>
        }
    },{
        path:"/my",
        exact:true,
        render(props){
            return <My/>
        }
    },{
        path:"/download",
        exact:true,
        render(props){
            return <Download/>
        }
    },{
        path:"/friend",
        exact:true,
        render(props){
            return <Friend/>
        }
    }
]
let types = ["index","top","playlist","djradio","artist","album"]

let childRouter = [
    {
        path:"/index",
        title:"推荐",
        exact:true,
        active:"active"
    },{
        path:"/top/list/:idx",
        title:"排行榜",
        exact:true,
        active:"active"
    },{
        path:"/playlist",
        title:"歌单",
        exact:true,
        active:"active"
    },{
        path:"/djradio",
        title:"主播电台",
        exact:true,
        active:"active"
    },{
        path:"/artist",
        title:"歌手",
        exact:true,
        active:"active"
    },{
        path:"/album",
        title:"新碟上架",
        exact:true,
        active:"active"
    }
]

let childRender = [
    {
        path:"/index",
        exact:true,
        render(){
            return <Index/>
        }
    },{
        path:"/index/top",
        exact:true,
        render(){
            return <TopList/>
        }
    },{
        path:"/index/playlist",
        exact:true,
        render(){
            return <PlayList/>
        }
    },{
        path:"/index/djradio",
        exact:true,
        render(){
            return <Djradio/>
        }
    },{
        path:"/index/artist",
        exact:true,
        render(){
            return <Artist/>
        }
    },{
        path:"/index/album",
        exact:true,
        render(){
            return <Album/>
        }
    }
]
export {headerRouter,childRouter,types,childRender};
