import React, { useEffect, Fragment } from "react"
import {useSelector} from "react-redux"
import {useBanner} from "../store/action/index"
export default function BannerPage(props){
    //获取状态管理仓库中的banner状态管理函数数据
    let state = useSelector((state)=>{   
        return state
    })
    let {bannerReducer} = state;
    let getData = useBanner()
    useEffect(()=>{
        getData()
    },[])
    useEffect(()=>{
        if(bannerReducer.data.length===undefined){
            let aList = document.querySelectorAll(".banner a")
            //初始化界面的节点
            let imgList = document.querySelector(".ban-img");
            let circle = document.querySelectorAll(".banner .circle");
            let prev = document.querySelector(".rev-left");
            let preNext = document.querySelector(".rev-right");
            console.log(aList,imgList,circle)
