import React from "react"
import {useLocation,Link, NavLink} from "react-router-dom"
import qs from "qs"
export default function PageNation(props){
    let {num,fn} = props
    let pageList = 20; //每篇显示多少个
    let pageLocation = Math.ceil(num/pageList)
    console.log(pageLocation)
    //获取地址来判断是哪个分类的分页
    let {pathname} = useLocation()
    let {cat,limit} = qs.parse(useLocation().search.slice(1))
    console.log(pathname,cat,limit)
    return(
        [...".".repeat(pageLocation)].map((item,index)=>{
            return <NavLink activeClassName="active" to={`${pathname}?cat=${cat}&limit=${limit}&page=${index+"abc"}`} key={index} onClick={()=>{
                fn(index)
            }}>{index+1}</NavLink>
        })
    )
}
