import React, { useEffect } from "react"
import {useSelector} from "react-redux"
import {useRecommend} from "../store/action/index"
function Recommend(){
    let state = useSelector((state)=>{
        return state    //获取状态管理函数中的状态数据
    })
    let getData = useRecommend();  //获取操作数据的函数

    useEffect(()=>{
        getData("/personalized?limit=8")   //在组件跟新是使用操作函数来获取数据然后返回到状态管理函数中
    },[])
    return(
        
        <div className="dic-muc">
            <div className="newDic-prev"></div>
                <div className="newDic">
                    {}
                </div>
        </div>
    )
}

export default Recommend;
