import React, { Fragment,useEffect,useState} from "react"
import { useLocation} from "react-router-dom"
import BannerPage from "../../component/banner"
import Recommend from "../../component/recommend"
import NewDiscs from "../../component/newdic"
import TopList from "../../component/toplist"
import Footer from "../../component/footer"
import Aside from "../../component/aside"
import {getUrl} from "../../store/action/index"
import {CommendTitle,TopListTitle,NewDisc} from "../../component/title"
import qs from "qs"

function Index(props){
    let [musicLocation,setMuc] = useState("");
    useEffect(()=>{
        // console.log(musicLocation)
    },[musicLocation])

    function getmusic(data){
        getUrl(`/song/url?id=${data}`).then((res)=>{
            function muc(Data){
                return new Promise((reslove,reject)=>{
                    setMuc(Data)
                })
            }
            muc(res.data.data[0].url).then(()=>{
                console.log(musicLocation)
                if(musicLocation===null){
                    alert("为获取到版权,努力中！")
                }
            })
        })
    }
    //首页
    return(
        <Fragment>
            <BannerPage/>
            <main>
                <div className="content">
                    <div className="cont-left">
                        <div className="recommend-wrap">
                            <CommendTitle/>
                            <Recommend url={"/personalized"} cat={""} limit={8}/>
                            <NewDisc/>
                            <NewDiscs/>
                            <TopListTitle/>
                            <TopList fn={getmusic}/>
                        </div>
                    </div>
                    <Aside/>
                </div>
            </main>
            <Footer/>
        </Fragment>
    )
}

export default Index
