import { useCallback } from 'react';
import useToggle from '../useToggle';

var useBoolean = function useBoolean() {
  var defaultValue = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : false;

  var _useToggle = useToggle(defaultValue),
      state = _useToggle.state,
      toggle = _useToggle.toggle;

  var setTrue = useCallback(function () {
    return toggle(true);
  }, [toggle]);
  var setFalse = useCallback(function () {
    return toggle(false);
  }, [toggle]);
  return {
    state: state,
    toggle: toggle,
    setTrue: setTrue,
    setFalse: setFalse
  };
};

export default useBoolean;
