import React, { useState, useEffect } from "react"
import play from "../static/css/play.module.css"
export default function Play(){
    let [show,setShow] = useState(false)
    useEffect(()=>{
        let audio = document.querySelector("audio")
        let playwrap = document.querySelector(".playWrap")
        let cover = playwrap.querySelector(".cover")
        console.log(playwrap)
        playwrap.onclick=function(){
            setShow(!show)
            if(show){
                let deg = window.getComputedStyle(cover,"style").transform
                console.log(deg)
            }else{

            }
            
            
        }
    })
    return(
        <div id={play.wrap} 
        className="playWrap">
            <audio autoPlay="autoplay" src="http://m801.music.126.net/20200409185851/0190708a07cd5b69978ced9a24e01ee0/jdymusic/obj/w5zDlMODwrDDiGjCn8Ky/1908443934/5f83/3b86/fa9e/ee9fcf595c6d85b79dc462b39ba71ae5.mp3">
				
            </audio>
            <div id={play.imgcover}>
                <div id={play.cover} className="cover"></div>
            </div>
            <span id={play.play} className="play" style={show?{display:"block"}:{display:"none"}}></span>
            <span id={play.end} className="end" style={!show?{display:"block"}:{display:"none"}}></span>
        </div>
    )
}
