        //             let clonelist = document.createElement("li")
        //             data.albums.slice(0,5).map((item,index)=>{
        //                 clonelist.innerHTML += `<li class="newDicList" key=${index}>
        //                         <div class="newDic-muc">
        //                             <img src=${item.picUrl} alt=""/>
        //                             <a href="" class="newDic-cover"></a>
