import React, { useEffect } from "react"
import {useSelector} from "react-redux"
import {useRecommend} from "../store/action/index"
function Recommend(){
    let state = useSelector((state)=>{
        return state.recommendReducer    //获取状态管理函数中的状态数据
    })
    let getData = useRecommend();  //获取操作数据的函数

    useEffect(()=>{
        getData("/personalized?limit=8")   //在组件跟新是使用操作函数来获取数据然后返回到状态管理函数中
    },[])
    let {data} = state
    console.log(data)
    return(
        <div>
            <ul class="rec-mucWrap">
                {data.length!==undefined?"":(data.result.map((item,index)=>{
                    return (
                        <li key={item.id}>
                            <div class="rec-muc">
                                <img src={item.picUrl} alt=""/>
                                <a href="" class="cover"></a>
                                <div class="rec-icon">
                                    <span class="set-icon"></span>
                                    <span class="icon-txt">71万</span>
                                    <a href="" class="p-icon"></a>
                                </div>
                                <p class="rec-mucTxt">
                                    <a href="">
                                        {item.name}
                                    </a>
                                </p>
                            </div>
                        </li>
                    )
                }))}
            </ul>
        </div>
    )
}

export default Recommend;
