import React, { Fragment, useState } from "react"
import {useLocation,Link, NavLink} from "react-router-dom"
import qs from "qs"
export default function PageNation(props){
    let {num,fn} = props
    let pageList = 20; //每篇显示多少个
    let pageLocation = Math.ceil(num/pageList)
    //获取地址来判断是哪个分类的分页
    let {pathname} = useLocation()
    let {cat,limit,page} = qs.parse(useLocation().search.slice(1))
    let [locationPage,setPage] = useState(0)
    console.log(page)
    return(
        <Fragment>
            <NavLink to={`${pathname}?cat=${cat}&limit=${limit}&page=${(page>1&&page<=5?Number(--page):page)}`}
                onClick={()=>{
                    let newpage = (page>1&&page<=5?Number(page)-1:page)
                    console.log(newpage)
                    fn(newpage)
                }}
            >上一页</NavLink>
            {
                [...".".repeat(pageLocation)].map((item,index)=>{
                return <NavLink activeClassName="active" to={`${pathname}?cat=${cat}&limit=${limit}&page=${index+1}`} key={index} 
                    onClick={()=>{
                        fn(index)
                    }}
                >{index+1}</NavLink>
                })
            }
            <NavLink to={`${pathname}?cat=${cat}&limit=${limit}&page=${(page<5&&page>0?Number(page)+1:page)}`}>下一页</NavLink>
        </Fragment>
        
    )
}
