var useDrag = function useDrag() {
  var getProps = function getProps(data) {
    return {
      key: JSON.stringify(data),
      draggable: 'true',
      onDragStart: function onDragStart(e) {
        e.dataTransfer.setData('custom', JSON.stringify(data));
      }
    };
  };

  return getProps;
};

export default useDrag;
