import React, { Fragment, useEffect } from 'react';
import { Route, Switch } from 'react-router-dom';
import "./static/css/index.css"
import {headerRouter,childRender} from "./router/index"
import Header from "./component/header"
import {useSelector} from "react-redux"
import Play from "./component/play"
import {useRequest} from "@umijs/hooks"
// y移动端组件
import Mheader from "./mobileconponemt/mheader"
import MNav from "./mobileconponemt/mnav"

function App() {
let state = (useSelector((state)=>{
	return state
}))
let apploading = state.bannerReducer.loading;
console.log(apploading)
useEffect(()=>{
	console.log(state.bannerReducer.data)
})
return (
	<Fragment>
		<div className="App">
			<Play/>
			<div className={apploading?"apploading":"apploadingend"}>
				<p id="loading">Loading<span>.</span><span>.</span><span>.</span></p>
			</div>
			<Header/>
			<Switch>
				{headerRouter.map((item,index)=>{
					return(
						<Route
							key={index}
							path={item.path}
							exact={item.exact}
							render={(props)=>{
								return item.render(props)
							}}
						></Route>
					)
				})}
				{childRender.map((item,index)=>{
					return(
						<Route
							key={index}
							path={item.path}
							exact={item.exact}
							render={(props)=>{
								return item.render(props)
							}}
						></Route>
					)
				})}
			</Switch>
		</div>
	</Fragment>
  );
}
export default App;
