import React from "react"
import {useLocation,Link} from "react-router-dom"
import qs from "qs"
export default function PageNation(props){
    let {num,fn} = props
    let pageList = 20; //每篇显示多少个
    let page = Math.ceil(num/pageList)
    //获取地址来判断是哪个分类的分页
    let {pathname} = useLocation()
    let {cat,limit} = qs.parse(useLocation().search.slice(1))
    console.log(limit)
    console.log(pathname,cat)
    return(
        [...".".repeat(page)].map((item,index)=>{
            return <Link to={`${pathname}?cat=${cat}&limit=${limit}&page=${index}`} key={index} onClick={()=>{
                fn(index)
            }}>{index+1}</Link>
        })
    )
}
