        exact:true,
        active:"active"
    },{
        path:"/index/artist",
        title:"歌手",
        exact:true,
        active:"active"
    },{
        path:"/index/album",
        title:"新碟上架",
        exact:true,
        active:"active"
    }
*/

let childRender = [
    {
        path:"/index",
        title:"推荐",
        exact:true,
        render(props){
            return (
                <Fragment>
                    <ChildNav/>
                    <Index/>
                </Fragment>
            )
        }
    },{
        path:"/playlist",
        exact:true,
        title:"歌单",
        render(props){
            return (
                <Fragment>
                    <ChildNav/>
                    <PlayList/>
                </Fragment>
            )
        }
    },{
        path:"/playlist/detail",
        exact:true,
        title:"歌单",
        render(props){
            return (
                <Fragment>
                    <ChildNav/>
                    <Detail/>
                </Fragment>
            )
        }
    }
    // },{
