import React, { useEffect } from "react"
import {useSelector} from "react-redux"
import {useTopList} from "../store/action/index"
import {useLocation} from "react-router-dom"
import qs from "qs"
function TopList(){
    let loca = useLocation().pathname
    let state = useSelector((state)=>{
        return state.ToplistReducer
    })
    let getData = useTopList()
    
    let Orgdata = null;
    let Updata = null;
    let Newdata = null;
    useEffect(()=>{
        getData("/top/list?idx=3","/top/list?idx=0","/top/list?idx=2")
        if(state.Updata){
            if(state.Updata.length===undefined){
                Updata = state.Updata;
            }
        }else if(state.Orgdata){
            if(state.Orgdata.length===undefined){
                Orgdata = state.Orgdata;
            }
        }else if(state.Newdata){
            if(state.Newdata.length===undefined){
                Newdata = state.Newdata;
            }
        }
    },[loca])
    return(
        <div className="notic-muc">
             <div className="notice-top-wrap">
             <div className="notice-top">
                <div className="notice-log Top-logo">
                    <img src={Updata===null?"":Updata.playlist.coverImgUrl} alt=""/>
                    <a href="" className="notice-cover"></a>
                </div>
                <div className="notice-tit">
                        <a href=""><h3>{Updata===null?"":Updata.playlist.name}</h3></a>
                        <div className="notice-tit-cover">
                            <a href="" className="notice-play"></a>
                            <a href="" className="notice-add"></a>
                        </div>
                </div>
                </div>
             </div>
        </div>
    )
}

export default TopList;
