import React, { Fragment } from 'react';
import { Route, Switch } from 'react-router-dom';
import "./static/css/index.css"
import {headerRouter,childRender} from "./router/index"
import Header from "./component/header"
import Index from "./views/index/index"
import {useSelector} from "react-redux"
import {Spin} from "antd"

function App() {
let state = (useSelector((state)=>{
	return state
}))
let loading = state.bannerReducer.loading
return (
	<Fragment>
		<div className={loading?"loading":"loadingend"}>
		<svg viewBox="0 0 1024 1024" focusable="false" class="anticon-spin" data-icon="loading" width="1em" height="1em" fill="currentColor" aria-hidden="true"><path d="M988 548c-19.9 0-36-16.1-36-36 0-59.4-11.6-117-34.6-171.3a440.45 440.45 0 00-94.3-139.9 437.71 437.71 0 00-139.9-94.3C629 83.6 571.4 72 512 72c-19.9 0-36-16.1-36-36s16.1-36 36-36c69.1 0 136.2 13.5 199.3 40.3C772.3 66 827 103 874 150c47 47 83.9 101.8 109.7 162.7 26.7 63.1 40.2 130.2 40.2 199.3.1 19.9-16 36-35.9 36z"></path></svg>
		</div>
		<div className="App">
			<Header/>
			<Switch>
				{headerRouter.map((item,index)=>{
					return(
						<Route
							key={index}
							path={item.path}
							exact={item.exact}
							render={(props)=>{
								return item.render(props)
							}}
						></Route>
					)
				})}
				{childRender.map((item,index)=>{
					return(
						<Route
							key={index}
							path={item.path}
							exact={item.exact}
							render={(props)=>{
								return item.render(props)
							}}
						></Route>
					)
				})}
			</Switch>
		</div>
	</Fragment>
  );
}

export default App;
